import React from 'react';
import { Card, Button, CardTitle, Form, FormGroup, Label, Input } from 'reactstrap';

import './CardForm.css';

const MessageCardForm = (props) => {
  return (
    <Card body className="location-form">
      <CardTitle>Sailboat Geolocation</CardTitle>
      {//<CardText>Please input the longitude and latitude for the sailboat.</CardText>
      }
      {
        !props.sendingMessage && !props.sentMessage && props.haveUsersLocation ?
          <Form onSubmit={props.formSubmitted}>
            <FormGroup>
                <Label for="slat">Latitude</Label>
                <Input 
                    onChange={this.valueChanged}
                    type="number" 
                    step="0.0000001"
                    name="slat" 
                    id="slat" 
                    placeholder="Enter the latitude" />
            </FormGroup>
            <FormGroup>
                <Label for="slng">Longitude</Label>
                <Input 
                    onChange={this.valueChanged}
                    type="number"
                    step="0.0000001" 
                    name="slng" 
                    id="slng" 
                    placeholder="Enter the longitude" />
            </FormGroup>
            <Button type="cancel" color="danger" onClick={props.cancelMessage}>Cancel</Button> {' '}
            <Button type="submit" color="info" disabled={!props.formIsValid()}>Send</Button>
          </Form> :
          props.sendingMessage || !props.haveUsersLocation ? 
            <CardText>Thanks for submitting a message!</CardText> : ""
      }
    </Card>
  );
};

export default MessageCardForm;
