import React, { Component } from 'react';
import L from 'leaflet';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import pin from './pinpoint.png'; 
import pin2 from './pinpoint2.png'; 

var myIcon = L.icon({
    iconUrl: pin,
    iconSize: [30, 41],
    iconAnchor: [12.5, 40],
    popupAnchor: [0, -45],
  })
  
  var myIcon2 = L.icon({
    iconUrl: pin2,
    iconSize: [30, 41],
    iconAnchor: [12.5, 40],
    popupAnchor: [0, -45],
  })

class MapView extends Component {

    state = {
        location: {
          lat: 5.354482, 
          lng: 100.301226,
        },
        haveUsersLocation: false,
        zoom: 1,
        userSetDest: {
          slat: '',
          slng: '',
        },
        locations: [],
        showLocationForm: false,
        sendingLocation: false,
        sentLocation: false,
        //sideDrawer 
        sideDrawerOpen: false
    };
      
    // Get user geolocation and reconfigurate some attributes
    componentDidMount(){
        // Location from the database
        getLocation()
            .then(locations => {
                this.setState({
                    locations,
                    haveUsersLocation: true,
                    zoom: 18
                });
            });
    };
    
    showLocationForm = () => {
        this.setState({
        showLocationForm: true
        });
    }
    
    cancelLocation = () => {
        this.setState({
            showLocationForm: false
        });
    }
    
    formIsValid = () => {
        const userSetDest = {
            latitude: this.state.userSetDest.slat,
            longitude: this.state.userSetDest.slng
        };
        const result = Joi.validate(userSetDest, schema);
        //console.log(userSetDest);
        return !result.error && userSetDest ? true : false;
    };
    
    formSubmitted = (event) => {
        event.preventDefault();
        //console.log(this.state.userSetDest);
        
        if (this.formIsValid()){
            //if no error
            this.setState({
                sendingLocation: true
            });
    
        const dest = {
            latitude: this.state.userSetDest.slat,
            longitude: this.state.userSetDest.slng
        };
    
        sendLocation(dest)
            .then((result) => {
                setTimeout(() => {
                    this.setState({
                    sendingLocation: false,
                    sentLocation: true
                    });
                }, 4000);
            });
        }
    };
    
    valueChanged = (event) => {
        const { name, value } = event.target; 
        this.setState((prevState) => ({
            userSetDest: {
                ...prevState.userSetDest,
                [name]: value,
            }
        }))
    };

    render() {
        const position = [this.state.location.lat, this.state.location.lng];
        const dest = this.state.locations[this.state.locations.length-1];
        return (
            <div>
                <Map className="map" center={props.position} zoom={props.zoom}>
                    <TileLayer
                    attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    { 
                    props.haveUsersLocation ?
                    <Marker 
                        position={props.position} 
                        icon={myIcon}>
                        <Popup>
                        lat:{props.location.lat}, lng:{props.location.lng}
                        </Popup>
                    </Marker> : ""
                    }
                    {props.destination.map(location => (
                    <Marker 
                        position={[props.dest.latitude, props.dest.longitude]} 
                        icon={myIcon2}>
                        <Popup>
                        lat:{props.dest.latitude}, lng:{props.dest.longitude}
                        </Popup>
                    </Marker>
                    ))} 
                </Map>
                {
                !this.state.showLocationForm ?
                <Button className="location-form" onClick={this.showLocationForm} color="info">Add a location</Button> :
                !this.state.sentLocation ?
                <CardForm
                    cancelLocation={this.cancelLocation}
                    showLocationForm={this.state.showLocationForm}
                    sendingLocation={this.state.sendingLocation}
                    sentLocation={this.state.sentLocation}
                    haveUsersLocation={this.state.haveUsersLocation}
                    formSubmitted={this.formSubmitted}
                    valueChanged={this.valueChanged}
                    formIsValid={this.formIsValid}
                /> :
                this.setState({
                    sentLocation: false,
                    showLocationForm: false
                })
                } 
            </div>
        );
    }
}

export default MapView;
