import React, { Component } from 'react';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import { Button } from 'reactstrap';
import Joi from 'joi';
import L from 'leaflet';
import pin from './pinpoint.png'; 
import pin2 from './pinpoint2.png'; 
import { Route, HashRouter } from "react-router-dom";
import './App.css';

// Component for side drawer and toolbar
import Toolbar from './components/Toolbar/Toolbar';
import SideDrawer from './components/SideDrawer/SideDrawer';
import Backdrop from './components/Backdrop/Backdrop';
import MapView from './components/Contents/MapView';
import DataView from './components/Contents/DataView';
//import classnames from 'classnames';

var myIcon = L.icon({
  iconUrl: pin,
  iconSize: [30, 41],
  iconAnchor: [12.5, 40],
  popupAnchor: [0, -45],
})

var myIcon2 = L.icon({
  iconUrl: pin2,
  iconSize: [30, 41],
  iconAnchor: [12.5, 40],
  popupAnchor: [0, -45],
})

const schema = Joi.object().keys({
  latitude: Joi.number().min(-90).max(90).required(),
  longitude: Joi.number().min(-180).max(180).required()
});

class App extends Component {

  // Sidedrawer function
  drawerToggleClickHandler = () => {
    this.setState((prevState) => {
      return {sideDrawerOpen: !prevState.sideDrawerOpen};
    });
  };

  backdropClickHandler = () => {
    this.setState({sideDrawerOpen: false});
  };

  render(){

    // sidedrawer backdrop variable
    let backdrop;

    if (this.state.sideDrawerOpen) {
      backdrop = <Backdrop click={this.backdropClickHandler}/>;
    }

    return (
      <HashRouter>
        <div style={{height: '100%'}}>
          <Toolbar drawerClickHandler={this.drawerToggleClickHandler}/> 
          <SideDrawer show={this.state.sideDrawerOpen}/>
          {backdrop}
          <div className="content">
            <Route path="/map" component={MapView}/>
            <Route path="/data" component={DataView}/>

          </div>
        </div>
      </HashRouter>
    );
  }
}

export default App;
