import React, { Component } from 'react';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import Joi from 'joi';
import L from 'leaflet';
import { Card, Button, CardTitle } from 'reactstrap';
import { Form, FormGroup, Label, Input } from 'reactstrap';
import pin from './pinpoint.png'; 
import './App.css';

// Component for side drawer and toolbar
import Toolbar from './components/Toolbar/Toolbar';
import SideDrawer from './components/SideDrawer/SideDrawer';
import Backdrop from './components/Backdrop/Backdrop';
//import classnames from 'classnames';

var myIcon = L.icon({
  iconUrl: pin,
  iconSize: [30, 41],
  iconAnchor: [12.5, 40],
  popupAnchor: [0, -45],
})

const schema = Joi.object().keys({
  latitude: Joi.number().min(-90).max(90).required(),
  longitude: Joi.number().min(-180).max(180).required()
});

const API_URL = window.location.hostname === 'localhost' ? 'http://10.234.35.203:80/api/v1/locations' : 'production-url-here'

class App extends Component {
  state = {
    location: {
      lat: 5.354482, 
      lng: 100.301226,
    },
    haveUsersLocation: false,
    zoom: 1,
    userSetLocation: {
      slat: '',
      slng: '',
    },
    locations: [],
    //sideDrawer 
    sideDrawerOpen: false
  }

  // Get user geolocation and reconfigurate some attributes
  componentDidMount(){
    //Fetching the sailboat latest location
    fetch(API_URL)
      .then(res => res.json())
      .then(locations => {
        this.setState({
          locations
        });
      });

    navigator.geolocation.getCurrentPosition( (position) => {
      this.setState({
        location: {
          lat: position.coords.latitude,
          lng: position.coords.longitude
        },
        haveUsersLocation: true,
        zoom: 18,
      });
    }, () => {
      console.log('Sorry but user does not give their location...'+
      'Showing the IP location...');

      fetch('https://ipapi.co/json')
        .then(res => res.json())
        .then(location => {
          this.setState({
            location: {
              lat: location.latitude,
              lng: location.longitude
            },
            haveUsersLocation: true,
            zoom: 18,
          });
        })
    });
  }

  formIsValid = () => {
    const userSetLocation = {
      latitude: this.state.userSetLocation.slat,
      longitude: this.state.userSetLocation.slng
    };
    const result = Joi.validate(userSetLocation, schema);
    //console.log(userSetLocation);
    return !result.error && userSetLocation ? true : false;
  }

  formSubmitted = (event) => {
    event.preventDefault();
    //console.log(this.state.userSetLocation);

    if (this.formIsValid()){
      //if no error
      fetch(API_URL, {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
        },
        body: JSON.stringify({
          //user specified location
          latitude: this.state.userSetLocation.slat,
          longitude: this.state.userSetLocation.slng
          // original boat location
          //latitude: this.state.location.lat,
          //longitude: this.state.location.lng
        })
      }).then(res => res.json())
      .then(location => {
        console.log(location);
      })
    }
  }

  valueChanged = (event) => {
    const { name, value } = event.target; 
    this.setState((prevState) => ({
      userSetLocation: {
        ...prevState.userSetLocation,
        [name]: value,
      }
    }))
  }

  // Sidedrawer function
  drawerToggleClickHandler = () => {
    this.setState((prevState) => {
      return {sideDrawerOpen: !prevState.sideDrawerOpen};
    });
  };

  render(){
    const position = [this.state.location.lat, this.state.location.lng];
    // sidedrawer variable
    let sideDrawer;
    let backdrop;

    if (this.state.sideDrawerOpen) {
      sideDrawer = <SideDrawer />;
      backdrop = <Backdrop />;
    }

    return (
      <div style={{height: '100%'}}>
        <Toolbar /> 
        {sideDrawer}
        {backdrop}
        <div className="map">
          <Card body className="location-form">
            <CardTitle>Sailboat Geolocation</CardTitle>
              {//<CardText>Please input the longitude and latitude for the sailboat.</CardText>
              }
              <Form onSubmit={this.formSubmitted}>
                <FormGroup>
                  <Label for="slat">Latitude</Label>
                    <Input 
                      onChange={this.valueChanged}
                      type="number" 
                      step="0.0000001"
                      name="slat" 
                      id="slat" 
                      placeholder="Enter the latitude" />
                </FormGroup>
                <FormGroup>
                  <Label for="slng">Longitude</Label>
                    <Input 
                      onChange={this.valueChanged}
                      type="number"
                      step="0.0000001" 
                      name="slng" 
                      id="slng" 
                      placeholder="Enter the longitude" />
                </FormGroup>
                <Button type="submit" color="info" disabled={!this.formIsValid()}>Send</Button>
              </Form>
          </Card>
          <Map className="map" center={position} zoom={this.state.zoom}>
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            { 
              this.state.haveUsersLocation ?
              <Marker 
                position={position} 
                icon={myIcon}>
                <Popup>
                  lat:{this.state.location.lat}, lng:{this.state.location.lng}
                </Popup>
              </Marker> : ''
            }
            {this.state.locations.map(location => (
              <Marker 
                position={[location.latitude, location.longitude]} 
                icon={myIcon}>
                <Popup>
                  lat:{location.latitude}, lng:{location.longitude}
                </Popup>
              </Marker>
            ))} 
          </Map>     
        </div>
      </div>
    );
  }
}

export default App;
